    let score1 = localStorage.getItem('score1');
    let score2 = localStorage.getItem('score2');

    score1 = score1 == null ? 0 : score1;
    score2 = score2 == null ? 0 : score2;

    const winner =
      score1 > score2
        ? localStorage.getItem('nomJoueur1')
        : score1 == score2
