import React, { useState, useEffect } from 'react';
import './styles/main-style.css';
// import './styles/background.css';
import Losanges from './Losanges';
import Title from './Title';
import Settings from './Settings';
import GameContainer from './GameContainer';

const App = () => {
    const numRows = 22;
    const numCols = 15;
    const totalPlayers = 2;
    let switchScreen = 1;
    const [plateau, setPlateau] = useState(Array.from({ length: 1 + numRows + 1 }, () => Array(1 + numCols + 1).fill(0)));

    localStorage.setItem("currentPlayer", 1);

    //test
    localStorage.setItem("nomJoueur1", "Rouge");
    localStorage.setItem("nomJoueur2", "Bleu");
    localStorage.setItem("couleurJoueur1", "Rouge");
    localStorage.setItem("couleurJoueur2", "Bleu");
    localStorage.setItem("nomJoueur3", "Vert");
    localStorage.setItem("nomJoueur4", "Noir");
    localStorage.setItem("couleurJoueur3", "Vert");
    localStorage.setItem("couleurJoueur4", "Noir");

    for (let i = 1; i <= totalPlayers; i++) {
        localStorage.setItem("scorePlayer" + i, 0);
    }

    useEffect(() => {
        switchScreen = localStorage.getItem("switchScreen") != null ? localStorage.getItem("switchScreen") : switchScreen;
    }, []);

    return (
        <div className="App">
            <Losanges />  
            {/*<GameContainer
                numRows={numRows}
                numCols={numCols}
                plateau={plateau}
                setPlateau={setPlateau}
                totalPlayers={totalPlayers}
    />*/}
            {(switchScreen === 1) && <Title />}
        </div>
    );
};

export default App;
